import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c as t,e as _}from"./app-693028fd.js";const o={},c=_("<p>○蝉蚕香</p><p>交址所贡，唐宫中呼为瑞龙脑。</p><p>○茵犀香</p><p>西域献，汉武帝用之，煮汤辟疠。</p><p>○石叶香</p><p>魏文帝时，腹题国贡，状如云母，可以辟疫。</p><p>○百濯香</p><p>孙亮为四姬合四炁香衣，香百濯不落，因名。</p><p>○凤髓香</p><p>唐穆宗藏真岛出，焚之崇礼。</p><p>○紫述香</p><p>《述异记》云，又名麝香草。</p><p>○都夷香</p><p>《洞冥记》云：香如棘核，食之不饥。</p><p>○荃芜香</p><p>燕昭王时，出波弋国，浸地则土石皆香。</p><p>○辟邪香、瑞麟香、金凤香</p><p>唐同昌公主带玉香囊，芬馥满路。</p><p>○月支香</p><p>月支国进，如卵，烧之辟疫百里，九月不散。</p><p>○振灵香</p><p>《十洲记》云：聚窟洲有树如枫叶，香闻数百里。</p><p>○返魂香、震檀香、惊精香、返生香、却死香</p><p>月支国一香五名，尸埋地下者，闻之即活。</p><p>○千亩香</p><p>《述异记》云：以林名香。</p><p>○■〈香〈敝，去攵〉〉齐香</p><p>出波斯国，入药，治百病。</p><p>○龟甲香</p><p>《述异记》云：即桂香之善者。</p><p>○兜末香</p><p>《本草》汉武帝西王母降，焚是香也。</p><p>○沉光香</p><p>《洞冥记》云：涂魂国烧之有光。</p><p>○沉榆香</p><p>《拾遗记》：黄帝封禅，焚之。</p><p>○蘅芜香</p><p>汉武帝梦李夫人授此香。</p><p>○百蕴香</p><p>飞燕浴身用此。</p><p>○月麟香</p><p>文帝宫中爱女，号袖里春。</p><p>○辟寒香</p><p>焚之可以辟寒。</p><p>○龙文香</p><p>汉武帝时，外国进。</p><p>○千步香</p><p>南郡所贡，焚之，千步内，犹有香气。</p><p>○九和香</p><p>《三洞珠囊》曰：玉女擎玉炉焚之。</p><p>○九真香、青木香、沉水香</p><p>皆合德上飞燕襚中物。</p><p>○罽宾国香</p><p>杨牧席间焚之，上有楼台之状。</p><p>○拘勿头华香</p><p>拘勿头国进，香闻数里。</p><p>○精只香</p><p>出涂魂国，焚之辟鬼。</p><p>○飞气香</p><p>《珠囊》曰：真人所烧。</p><p>○五枝香</p><p>烧之十日，上彻九重。</p><p>○羯布罗香</p><p>《西域记》云：树如松，色如冰雪。</p><p>○大象藏香</p><p>因龙斗而生，若烧一丸，兴大光明，珠如甘露。</p><p>○兜娄婆香、牛头旃檀香</p><p>出《典释》。</p><p>○明庭香　明天发口香</p><p>出胥陀寒国。</p><p>○迷迭香</p><p>出西域，焚之去邪。</p><p>○必栗香</p><p>焚之，去一切恶气。</p><p>○揭车香</p><p>《本草》：焚之，去蛀辟臭。</p><p>○刀圭第一香</p><p>唐昭宗赐崔胤一粒，终日旖旎。</p><p>○曲水香</p><p>香盘即之，似曲水像。</p><p>○鹰嘴香</p><p>番人出，焚之辟疫。</p><p>○乳头香</p><p>曹务光理赵州，用盆焚云：财易得，佛难求。</p><p>○助情香</p><p>安禄山进，玄宗含之，筋力不倦。</p><p>○夜酣香</p><p>炀帝迷楼所梦也。</p><p>○雀头香</p><p>魏文帝遣使于吴，求雀头香。</p><p>○伴月香</p><p>徐铉月夜露坐，焚之，故名此。</p><p>○鸡舌香</p><p>汉侍中刁存事又尚书郎含鸡舌香奏事。</p><p>○安息香</p><p>出三佛齐国。</p><p>○亚湿香</p><p>出占城国。</p><p>○金颜香</p><p>出大食真腊国。</p><p>○神精香（一名荃蘼，一名春芜）</p><p>出波弋，即前荃芜香也。其皮如丝，可以为布。</p><p>○沉光香、明庭香、金磾香、涂魂香</p><p>元封中，外国所献。</p><p>○蓬莱香</p><p>即沉水香结未成者，成片如小芝及大菌之状。</p><p>○鹧鸪斑香、思劳香</p><p>出日南，如乳香。</p><p>○橄榄香</p><p>状如黑胶，炙烧毫粒，经旬不散。</p>",110),r=[c];function s(n,a){return e(),t("div",null,r)}const f=p(o,[["render",s],["__file","名香谱.html.vue"]]);export{f as default};
