const t=JSON.parse('{"key":"v-23b7b9a0","path":"/%E8%89%BA%E8%97%8F/%E7%BB%98%E7%94%BB/%E5%90%8E%E7%94%BB%E5%BD%95.html","title":"后画录","lang":"zh-CN","frontmatter":{"title":"后画录","index":true,"icon":"editor","order":14,"category":["艺藏","绘画"],"tag":["僧彦悰","唐"],"description":"后画录 唐 僧彦悰 ●序 彦悰为帝京寺录。因观在京名迹，其中优劣差降，甚有不同，若曹、姚之徒，巳标前录，张、谢之伍，题之《续品》，沙门之内，弃其数人，但非释氏所宜，故阙而不录。都合二十七人，名曰《后画录》。如郑法轮、太常成嵩、尹伯干长通、竺元标等，虽行于代，未曰名家，若兹之流，以俟来哲。时贞观九年春三月十有一日序。 ●周中大夫郑法士 取法张公，备该万物...","head":[["meta",{"property":"og:url","content":"https://ruguoaaa.github.io/xirang/xirang6/%E8%89%BA%E8%97%8F/%E7%BB%98%E7%94%BB/%E5%90%8E%E7%94%BB%E5%BD%95.html"}],["meta",{"property":"og:site_name","content":"息壤"}],["meta",{"property":"og:title","content":"后画录"}],["meta",{"property":"og:description","content":"后画录 唐 僧彦悰 ●序 彦悰为帝京寺录。因观在京名迹，其中优劣差降，甚有不同，若曹、姚之徒，巳标前录，张、谢之伍，题之《续品》，沙门之内，弃其数人，但非释氏所宜，故阙而不录。都合二十七人，名曰《后画录》。如郑法轮、太常成嵩、尹伯干长通、竺元标等，虽行于代，未曰名家，若兹之流，以俟来哲。时贞观九年春三月十有一日序。 ●周中大夫郑法士 取法张公，备该万物..."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2023-10-31T10:24:30.000Z"}],["meta",{"property":"article:author","content":"Paladin"}],["meta",{"property":"article:tag","content":"僧彦悰"}],["meta",{"property":"article:tag","content":"唐"}],["meta",{"property":"article:modified_time","content":"2023-10-31T10:24:30.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"后画录\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2023-10-31T10:24:30.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"Paladin\\",\\"url\\":\\"https://ruguoaaa.github.io/xirang\\"}]}"]]},"headers":[],"git":{"createdTime":1698747870000,"updatedTime":1698747870000,"contributors":[{"name":"ruguoaaa","email":"ruguoaaa@163.com","commits":1}]},"readingTime":{"minutes":2.74,"words":821},"filePathRelative":"艺藏/绘画/后画录.md","localizedDate":"2023年10月31日","autoDesc":true,"excerpt":""}');ex