import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o as _,c as o,a as e}from"./app-693028fd.js";const c={},s=e("p",null,"《琴体说》",-1),n=e("p",null,"琴之首曰凤额。下曰凤舌。其次曰承露。乃临岳之前也。俗谓之岳裙。轸穴俗谓之轸眼。岳之为体。巍然而高。如山岳之状。凤嗉。琴项也。谓之喉舌。可以教令也。仙人肩者。取其若肩背之正也。龙腰者。取其曲折如龙也。又曰玉女腰。取其纤也。自肩至腰。总象凤翅耸然而张。龙唇龙龈。乃琴末承弦之异名。焦尾人谓之冠。取其状名也。冠内两线自龙唇绕入谓之龙须。龙池者。龙为变化之物。潜于幽深之地。迹虽隐而声之所自出也。凤沼者。取凤之来仪沐浴自如也。轸池又曰轸杯。轸者急也。古以竹为之。取凤非梧桐不栖。非竹实不食之义也。轸池侧有鸭掌有护轸。足曰凤足。当足处曰凤腿。天地二柱。一圆一方。为琴之心膂也。",-1),r=[s,n];function l(a,i){return _(),o("div",null,r)}const m=t(c,[["render",l],["__file","琴体说.html.vue"]]);export{m as default};
