const t=JSON.parse('{"key":"v-5b451182","path":"/%E8%89%BA%E8%97%8F/%E9%9F%B3%E4%B9%90/%E7%90%B4%E8%AE%AE%E7%AF%87.html","title":"琴议篇","lang":"zh-CN","frontmatter":{"title":"琴议篇","index":true,"icon":"editor","order":27,"category":["艺藏","音乐"],"tag":["刘籍","宋"],"description":"《琴议篇》（南宋）刘籍 琴者，禁也。禁邪归正，以和人心。始乎伏羲，成于文武，形象天地，气包阴阳，神思幽深，声韵清越，雅而能畅，乐而不淫，扶正国风，翼赞王化。善听者，知吉凶休咎，国家存亡。善鼓者，变动阴阳，聚散鬼神。是以古人左琴右书，无故则不撤。琴之为义大矣哉！夫和而鸣者，谓之声；参叙相应，谓之韵；韵而成文，谓之音。夫人志于所守，蕴积于中，而形于言。言之...","head":[["meta",{"property":"og:url","content":"https://ruguoaaa.github.io/xirang/xirang6/%E8%89%BA%E8%97%8F/%E9%9F%B3%E4%B9%90/%E7%90%B4%E8%AE%AE%E7%AF%87.html"}],["meta",{"property":"og:site_name","content":"息壤"}],["meta",{"property":"og:title","content":"琴议篇"}],["meta",{"property":"og:description","content":"《琴议篇》（南宋）刘籍 琴者，禁也。禁邪归正，以和人心。始乎伏羲，成于文武，形象天地，气包阴阳，神思幽深，声韵清越，雅而能畅，乐而不淫，扶正国风，翼赞王化。善听者，知吉凶休咎，国家存亡。善鼓者，变动阴阳，聚散鬼神。是以古人左琴右书，无故则不撤。琴之为义大矣哉！夫和而鸣者，谓之声；参叙相应，谓之韵；韵而成文，谓之音。夫人志于所守，蕴积于中，而形于言。言之..."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2023-11-02T08:01:44.000Z"}],["meta",{"property":"article:author","content":"Paladin"}],["meta",{"property":"article:tag","content":"刘籍"}],["meta",{"property":"article:tag","content":"宋"}],["meta",{"property":"article:modified_time","content":"2023-11-02T08:01:44.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"琴议篇\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2023-11-02T08:01:44.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"Paladin\\",\\"url\\":\\"https://ruguoaaa.github.io/xirang\\"}]}"]]},"headers":[],"git":{"createdTime":1698747870000,"updatedTime":1698912104000,"contributors":[{"name":"ruguoaaa","email":"ruguoaaa@163.com","commits":2}]},"readingTime":{"minutes":2.73,"words":819},"filePathRelative":"艺藏/音乐/琴议篇.md","localizedDate":"2023年10月31日","autoDesc":true,"excerpt":""}');expo