const t=JSON.parse('{"key":"v-e98d4d28","path":"/%E8%89%BA%E8%97%8F/%E9%9F%B3%E4%B9%90/%E7%90%B4%E8%AF%80.html","title":"琴诀","lang":"zh-CN","frontmatter":{"title":"琴诀","index":true,"icon":"editor","order":20,"category":["艺藏","音乐"],"tag":["薛易简","唐"],"description":"《琴诀》唐 薛易简 《琴史》曰：薛易简以琴待诏翰林，盖在天宝中也。尝着《琴诀》七篇，辞虽近俚，义有可采，今掇有大槩着焉，曰：“琴之为乐，可以观风教，可以摄心魂，可以辨喜怒，可以悦情思，可以静神虑，可以壮胆勇，可以绝麠麈俗，可以格免神，此琴之善者也。鼓琴之士，志静气正，则听者易分，心乱神浊，则听者难辩矣。常人但见用指轻利，取声温润、音韵不绝、句度流美，但...","head":[["meta",{"property":"og:url","content":"https://ruguoaaa.github.io/xirang/xirang6/%E8%89%BA%E8%97%8F/%E9%9F%B3%E4%B9%90/%E7%90%B4%E8%AF%80.html"}],["meta",{"property":"og:site_name","content":"息壤"}],["meta",{"property":"og:title","content":"琴诀"}],["meta",{"property":"og:description","content":"《琴诀》唐 薛易简 《琴史》曰：薛易简以琴待诏翰林，盖在天宝中也。尝着《琴诀》七篇，辞虽近俚，义有可采，今掇有大槩着焉，曰：“琴之为乐，可以观风教，可以摄心魂，可以辨喜怒，可以悦情思，可以静神虑，可以壮胆勇，可以绝麠麈俗，可以格免神，此琴之善者也。鼓琴之士，志静气正，则听者易分，心乱神浊，则听者难辩矣。常人但见用指轻利，取声温润、音韵不绝、句度流美，但..."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2023-11-02T08:01:44.000Z"}],["meta",{"property":"article:author","content":"Paladin"}],["meta",{"property":"article:tag","content":"薛易简"}],["meta",{"property":"article:tag","content":"唐"}],["meta",{"property":"article:modified_time","content":"2023-11-02T08:01:44.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"琴诀\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2023-11-02T08:01:44.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"Paladin\\",\\"url\\":\\"https://ruguoaaa.github.io/xirang\\"}]}"]]},"headers":[],"git":{"createdTime":1698747870000,"updatedTime":1698912104000,"contributors":[{"name":"ruguoaaa","email":"ruguoaaa@163.com","commits":2}]},"readingTime":{"minutes":1.89,"words":568},"filePathRelative":"艺藏/音乐/琴诀.md","localizedDate":"2023年10月31日","autoDesc":true,"excerpt":""}');export{t as data};
