const t=JSON.parse('{"key":"v-3ba11846","path":"/%E8%89%BA%E8%97%8F/%E9%9F%B3%E4%B9%90/%E7%90%B4%E8%B5%8B-%E6%99%8B-%E5%B5%87%E5%BA%B7.html","title":"","lang":"zh-CN","frontmatter":{"description":"《琴赋（并序）》嵇康 余少好音声，长而玩之，以为物有盛衰，而此无变，滋味有厌，而此不勌。可以异养神气、宣和情志、处穷独而不闷者，莫近于音声也。是故复之而不足，则吟咏以肆志，吟咏之不足，则寄言以广意。然八音之器、歌舞之象，历世才士并为之赋颂，具体制风流，莫不相袭。称其才干，则以危苦为上；赋其声音，则以悲哀为主；美其感化，则以垂涕为贵。丽则丽矣，然来尽其理...","head":[["meta",{"property":"og:url","content":"https://ruguoaaa.github.io/xirang/xirang6/%E8%89%BA%E8%97%8F/%E9%9F%B3%E4%B9%90/%E7%90%B4%E8%B5%8B-%E6%99%8B-%E5%B5%87%E5%BA%B7.html"}],["meta",{"property":"og:site_name","content":"息壤"}],["meta",{"property":"og:description","content":"《琴赋（并序）》嵇康 余少好音声，长而玩之，以为物有盛衰，而此无变，滋味有厌，而此不勌。可以异养神气、宣和情志、处穷独而不闷者，莫近于音声也。是故复之而不足，则吟咏以肆志，吟咏之不足，则寄言以广意。然八音之器、歌舞之象，历世才士并为之赋颂，具体制风流，莫不相袭。称其才干，则以危苦为上；赋其声音，则以悲哀为主；美其感化，则以垂涕为贵。丽则丽矣，然来尽其理..."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2023-10-31T10:24:30.000Z"}],["meta",{"property":"article:author","content":"Paladin"}],["meta",{"property":"article:modified_time","content":"2023-10-31T10:24:30.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2023-10-31T10:24:30.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"Paladin\\",\\"url\\":\\"https://ruguoaaa.github.io/xirang\\"}]}"]]},"headers":[],"git":{"createdTime":1698747870000,"updatedTime":1698747870000,"contributors":[{"name":"ruguoaaa","email":"ruguoaaa@163.com","commits":1}]},"readingTime":{"minutes":30.84,"words":9251},"filePathRelative":"艺藏/音乐/琴赋-晋-嵇康.md","localizedDate":"2023年10月31日","autoDesc":true,"excerpt":""}');export{t as data};
